import React, {useState, useEffect} from "react";
import "./App.css";
import { Input, Button } from "antd";
import { Link } from "react-router-dom";

function ScreenHome() {
  const [signUpUserName, setSignUpUserName] = useState("");
  const [signUpEmail, setSignUpEmail] = useState("");
  const [signUpPassword, setSignUpPassword] = useState("");
  const [signInEmail, setSignInEmail] = useState("");
  const [signInPassword, setSignInPassword] = useState("");
  const [isLogin, setIsLogin] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const [errorMessageSignin, setErrorMessageSignin] = useState("");


  return (
    <div className="Login-page">
      {/* SIGN-IN */}

      <div className="Sign">
        <Input className="Login-input" placeholder="arthur@lacapsule.com" />

        <Input.Password className="Login-input" placeholder="password" />

        <Link to="/screensource">
          <Button style={{ width: "80px" }} type="primary">
            Sign-in
          </Button>
        </Link>
      </div>

      {/* SIGN-UP */}

      <div className="Sign">
        <Input className="Login-input" placeholder="Arthur G" />

        <Input.Password className="Login-input" placeholder="password" />

        <Link to="/screensource">
          <Button style={{ width: "80px" }} type="primary">
            Sign-up
          </Button>
        </Link>
      </div>
    </div>
  );
}

export default ScreenHome;
